import React, { useState, useEffect } from 'react'
import Axios from 'axios';

function HostManage() {

    const hostID = sessionStorage.getItem("userID");
    const [activity, setActivity] = useState([]);
    const [location, setLocation] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await Axios.get(`http://localhost:3001/host/activity/${hostID}`);
            const location_response = await Axios.get(`http://localhost:3001/location/${activity.location}`);
            setActivity(response.data[0]);
            setLocation(location_response.data[0]);

        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className='container'>
            <h1>My Group</h1>
            <div className='activity-detail'>
                <h3>{activity.name}</h3>
                <span>{activity.category}</span>
                <p>{activity.description}</p>
                
                    <span>{location.name}</span>
                    <span>{location.address}</span>
            </div>
        </div>
    )
}

export default HostManage
