import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import HostManage from '../components/HostManage';
import CreateActivity from '../components/CreateActivity';

function Hostpage() {
    const hostID = sessionStorage.getItem('userID');
    const [activity, setActivity] = useState(null);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const activity_response = await Axios.get(`http://localhost:3001/host/activity/${hostID}`);
            setActivity(activity_response.data[0]);
        } catch (error) {
            console.log(error);
        }
    };

    if (activity !== null) {
        return <HostManage activity={activity} />;
    } else {
        return <CreateActivity />;
    }
}

export default Hostpage;
