import React, { useState } from 'react';
import '../style/CreateActivity.css';
import Axios from 'axios';

function CreateActivity() {

    const [name, setName] = useState("");
    const [category, setCategory] = useState("");
    const [capacity, setCapacity] = useState(0);
    const [description, setDescription] = useState("");

    const handleCategoryClick = (tag) => {
        setCategory(tag);
    }

    const data_body = {
        name: name,
        category: category,
        capacity: capacity,
        description: description
    }

    const addActivity = () => {
        try {
            Axios.post('http://localhost:3001/createActivity', data_body);
        } catch (error) {
            console.log(error)
        }       
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        addActivity();
        console.log("Create activity succesfully!")

    }


    return (
        <div className='container'>
            <h1>Create Activity</h1>
            <form >
                <div className='section'>
                    <h2>Choose tag(s) <span>that best describe your activity</span></h2>
                    <hr />
                    <div className="tags">
                        <button onClick={() => handleCategoryClick('Boardgame')}
                            className={category === 'Boardgame' ? 'selected' : ''}
                            style={{ background: "#DD6168" }}>
                            Boardgame
                        </button>
                        <button onClick={() => handleCategoryClick('Language')}
                            className={category === 'Language' ? 'selected' : ''}
                            style={{ background: "#FFD13F" }}>
                            Language
                        </button>
                        <button onClick={() => handleCategoryClick('Movie Talk')}
                            className={category === 'Movie Talk' ? 'selected' : ''}
                            style={{ background: "#7383D2" }}>
                            Movie Talk
                        </button>
                        <button onClick={() => handleCategoryClick('Book Talk')}
                            className={category === 'Book Talk' ? 'selected' : ''}
                            style={{ background: "#BAE076" }}>
                            Book Talk
                        </button>
                        <button onClick={() => handleCategoryClick('Talk on Topic')}
                            className={category === 'Talk on Topic' ? 'selected' : ''}
                            style={{ background: "#4D8CAF" }}>
                            Talk on Topic
                        </button>
                        <button onClick={() => handleCategoryClick('Watching Movie')}
                            className={category === 'Watching Movie' ? 'selected' : ''}
                            style={{ background: "#DD6168" }}>
                            Watching Movie
                        </button>
                        <button onClick={() => handleCategoryClick('Others')}
                            className={category === 'Others' ? 'selected' : ''}
                            style={{ background: "#7B7979" }}>
                            Others
                        </button>
                    </div>

                    <p>Don't know if your activity belongs to which tag? Select <span>Others</span></p>
                </div>

                <div className='section'>
                    <h2>Activity's Details</h2>
                    <hr />
                    <div>
                        <label>Activity's Name</label>
                        <input type='text' name='act-name' placeholder="More creative name, more people are interested !"
                            onChange={(event) => setName(event.target.value)}></input>
                    </div>

                    <div>
                        <label>Description</label>
                        <textarea name='act-desc'
                            placeholder="Tell them what you are going to do ..."
                            onChange={(event) => setDescription(event.target.value)}></textarea>
                    </div>

                    <div className='inline'>
                        <label >Maximum no. of participants</label>
                        <input type='number' name="capacity" min="2" max="30"
                            onChange={(event) => setCapacity(event.target.value)}></input>
                    </div>

                    <div>
                        <label>Location</label>
                    </div>

                    <div>
                        <label>Equipment</label>
                    </div>
                </div>

                <div className="btn-con">
                    <button onClick={handleSubmit}>Create</button>
                </div>
            </form>
        </div>
    )
}

export default CreateActivity
