import './App.css';
import { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
import CreateActivity from './components/CreateActivity';
import HostManage from './components/HostManage';
import logo from './asset/logo.png';
import CreatePage from './pages/CreatePage';
import Axios from 'axios';


function App() {

  sessionStorage.setItem("userID", 6422782555);
  const hostID = sessionStorage.getItem("userID");

  const [activity, setActivity] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const activity_response = await Axios.get(`http://localhost:3001/host/activity/${hostID}`);
      setActivity(activity_response.data[0]);
    } catch (error) {
      console.log(error);
    }
  };

  const handleCreateLinkClick = () => {
    if (activity && activity.length > 0) {
      return '/host';
    } else {
      return '/create';
    }
  };

  return (
    <div className="body-container">
      <BrowserRouter>

        <div className='header'>
          <div className='logo'>
            <img src={logo} alt="Logo" width="150" />
          </div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/manage" onClick={handleCreateLinkClick}>Create</Link>
              </li>
              <li>
                <Link to="">Login</Link>
              </li>
            </ul>
          </nav>
        </div>

        <Routes>
          <Route path='/manage' element={<CreatePage />} />
          <Route path='/create' element={<CreateActivity />} />
          <Route path='/host' element={<HostManage />} />
        </Routes>

      </BrowserRouter>
    </div>
  );
}

export default App;
