import React, { useState, useEffect } from 'react';
import Axios from 'axios';

function HostPage() {
    const [hosts, setHosts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const userID = sessionStorage.getItem("userID");

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await Axios.get(`http://localhost:3001/hosts`);
            const hostsData = response.data.map((val) => val.hostID);
            setHosts(hostsData);
            setIsLoading(false); // Set loading to false after data fetch
        } catch (error) {
            console.log(error);
            setIsLoading(false); // In case of error, set loading to false
        }
    };

    if (isLoading) {
        return <div>Loading...</div>;
    }

    // Render based on hosts data and userID
    if (userID && hosts.includes(Number(userID))) {
        return <div>Already created</div>;
    } else {
        return <div>Not created yet</div>;
    }
}

export default HostPage;
