import React, { useState, useEffect } from 'react'
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';


function CreatePage() {

    const hostID = sessionStorage.getItem("userID");
    const [activity, setActivity] = useState([]);
    const navigate = useNavigate();


    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const activity_response = await Axios.get(`http://localhost:3001/host/activity/${hostID}`);
            setActivity(activity_response.data[0]);

        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        // Redirect based on activity after initial fetch
        if (activity && activity.length > 0) {
            navigate('/host');
        } else {
            navigate('/create');
        }
    }, [activity, navigate]);

    // Consider returning some loading indicator or initial content here as the redirects will occur in the useEffect

    return <div>Loading...</div>;
}

export default CreatePage
