import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';
import HostManage from '../components/HostManage';
import CreateActivity from '../components/CreateActivity';

function HostPage() {
    const [hosts, setHosts] = useState([]);
    const userID = sessionStorage.getItem('userID');
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await Axios.get('http://localhost:3001/hosts');
            const hostsData = response.data.map((val) => val.hostID);
            setHosts(hostsData);
        } catch (error) {
            console.log(error);
        }
    };

    // Redirect to CreateActivity or HostManage based on conditions
    useEffect(() => {
        if (userID && hosts.includes(Number(userID))) {
            navigate('/hostmanage'); // Redirect to HostManage page if the user is a host
        } else {
            navigate('/createactivity'); // Redirect to CreateActivity if the user is not a host
        }
    }, [userID, hosts, navigate]);

    // This component doesn't render anything directly due to redirects
    return null;
}

export default HostPage;
