import React, { useState, useEffect } from 'react'
import Axios from 'axios';

function HostManage() {

    const hostID = sessionStorage.getItem("userID");
    const [activity, setActivity] = useState([]);
    const [getlocation, setGetLocation] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await Axios.get(`http://localhost:3001/host/activity/${hostID}`);
            setActivity(response.data[0]);

            if (response.data[0]) {
                const location_response = await Axios.get(`http://localhost:3001/location/${activity.location}`);
                setGetLocation(location_response.data[0]);
            }

        } catch (error) {
            console.log(error);
        }
    };

    return (
        <div className='container'>
            <h1>My Group</h1>
            <div className='activity-detail'>
                <h3>{activity.name}</h3>
                <p>{activity.description}</p>
                <div>
                    <span>{activity.category}</span>
                    <span>{activity.location}</span>
                </div>
            </div>
            <hr />
            <div className='gr'>

            </div>
        </div>
    )
}

export default HostManage
