import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EditActivity({ activityID }) {
    const navigate = useNavigate();

    const [name, setName] = useState('');
    const [category, setCategory] = useState('');
    const [capacity, setCapacity] = useState(2);
    const [description, setDescription] = useState('');
    const [location, setLocation] = useState(0);
    const [equipment, setEquipment] = useState(0);
    const [locationList, setLocationList] = useState([]);
    const [equipmentList, setEquipmentList] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const activityResponse = await Axios.get(`http://localhost:3001/activity/${activityID}`);
            const activityData = activityResponse.data;

            // Populate form with activity data
            setName(activityData.name);
            setCategory(activityData.category);
            setCapacity(activityData.capacity);
            setDescription(activityData.description);
            setLocation(activityData.location);
            setEquipment(activityData.equipment);

            const locationResponse = await Axios.get('http://localhost:3001/locations');
            const equipmentResponse = await Axios.get('http://localhost:3001/equipments');

            setLocationList(locationResponse.data);
            setEquipmentList(equipmentResponse.data);
        } catch (error) {
            console.log(error);
        }
    };

    const handleLocationChange = (event) => {
        const selectedRoomID = event.target.value;
        setLocation(selectedRoomID);
    };

    const handleEquipmentChange = (event) => {
        const selectedCode = event.target.value;
        setEquipment(selectedCode);
    };

    const handleCategoryClick = (event) => {
        event.preventDefault();
        setCategory(event.target.value);
    };

    const dataBody = {
        name,
        category,
        capacity,
        description,
        location,
        equipment,
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            await Axios.put(`http://localhost:3001/activity/${activityID}`, dataBody);
            console.log('Activity updated successfully!');
            navigate('/manage');
        } catch (error) {
            console.error('Error while updating activity:', error);
        }
    };

    return (
        <div className="container">
            <h1>Edit Activity</h1>
            <form onSubmit={handleSubmit}>
                {/* Form fields similar to CreateActivity */}
                {/* ... */}
                <button type="submit">Update</button>
            </form>
        </div>
    );
}

export default EditActivity;
